					ctx.strokeStyle = propertyFromCssClass(node.connection[2], 'border-top-color');
					ctx.setLineDash ({ dashed: [3, 2] }[propertyFromCssClass(node.connection[2], 'border-top-style')] || []);
					ctx.stroke();
